const scrollButton=document.getElementById("scroll-top-btn"),navBar=document.getElementById("navbar");window.onscroll=(()=>{scrollToTop()});const scrollToTop=()=>{document.body.scrollTop>20||document.documentElement.scrollTop>20?(scrollButton.style.display="block",navBar.classList.add("elevate-navbar")):(scrollButton.style.display="none",navBar.classList.remove("elevate-navbar"))},setScrollState=()=>{document.body.scrollTop=0,document.documentElement.scrollTop=0,window.location.hash=""};